CREATE TABLE IF NOT EXISTS `schedule` (`scheduleId` INTEGER auto_increment , `title` VARCHAR(255), `peoples` INTEGER, `peoplesNow` INTEGER DEFAULT 0, `category` VARCHAR(255), `howMuch` FLOAT, `description` VARCHAR(255), `when` BIGINT, `status` ENUM('OPENED', 'CLOSED', 'CANCELLED') DEFAULT 'OPENED', `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, `userId` INTEGER, PRIMARY KEY (`scheduleId`), FOREIGN KEY (`userId`) REFERENCES `user` (`userId`) ON DELETE SET NULL ON UPDATE CASCADE) ENGINE=InnoDB;
