CREATE TABLE IF NOT EXISTS `worker_schedules` (`createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, `scheduleId` INTEGER , `userId` INTEGER , PRIMARY KEY (`scheduleId`, `userId`), FOREIGN KEY (`scheduleId`) REFERENCES `schedule` (`scheduleId`) ON DELETE CASCADE ON UPDATE CASCADE, FOREIGN KEY (`userId`) REFERENCES `user` (`userId`) ON DELETE CASCADE ON UPDATE CASCADE) ENGINE=InnoDB;
